{
          "Version":"2012-10-17",
          "Id":"PutObjPolicy",
          "Statement":[{
                "Sid":"DenyUnEncryptedObjectUploads",
                "Effect":"Deny",
                "Principal":"*",
                "Action":"s3:PutObject",
                "Resource":"arn:aws:s3:::SensitiveDataBucket",
                "Condition":{
                   "StringNotEquals":{
                      "s3:sse-encryption-cipher":"AES256"
                   }
                }
             }
          ]
       }

